'Bundle'.datatype({preventNative: true})
'bundleNumber'.field({key: true})
'product'.field({refersTo: 'products', caption: 'Bundle Product', indexed: true, showAsLink: true})
'overheadCost'.field({numeric: true, minDecimals: 2, maxDecimals: 6})
'totalCost'.field({numeric: true, minDecimals: 2, maxDecimals: 6, caption: 'Total Bundle Cost'})
'stockingUOM'.field({caption: 'Stocking and Sales UOM'})
'totalCostExclTax'.field({numeric: true, minDecimals: 2, maxDecimals: 6, caption: 'Total Bundle Cost (Excl Tax)'})
'bundleProductId'.field({numeric: true})
'quantityPickable'.field({numeric: true})
'quantityMakeable'.field({numeric: true})
'quantityReservedForCustomerOrders'.field({numeric: true})
'sellableQuantity'.field({numeric: true, skipConflictChecks: true})
'lastComponentUpdate'.field({skipConflictChecks: true})
'manageManufacturingWithWorkOrders'.field({yesOrNo: true})
'expectedDaysToManufacture'.field({numeric: true, decimals: 0})
'unbundleWhenReceivingOnPO'.field({yesOrNo: true, caption: 'Unbundle When Receiving on PO'})

